import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-social-icons',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './social-icons.component.html',
  styleUrls: ['./social-icons.component.css']
})
export class SocialIconsComponent {
  socialIcons = [
    { name: 'Google', icon: 'fa-brands fa-google-plus-g', href: '#' },
    { name: 'Facebook', icon: 'fa-brands fa-facebook-f', href: '#' },
    { name: 'GitHub', icon: 'fa-brands fa-github', href: '#' },
    { name: 'LinkedIn', icon: 'fa-brands fa-linkedin-in', href: '#' }
  ];
}
<div class="flex justify-center space-x-2 xs:space-x-3 my-4 xs:my-5">
  <a 
    *ngFor="let social of socialIcons" 
    [href]="social.href" 
    class="border border-gray-300 rounded-lg w-8 h-8 xs:w-10 xs:h-10 flex items-center justify-center text-gray-600 hover:text-primary hover:border-primary hover:shadow-md transition-all duration-300 text-sm xs:text-base"
    [title]="social.name"
  >
    <i [class]="social.icon"></i>
  </a>
</div>
